import { useContext, useState } from "react";
import { ModalStateContext } from "../providers/State";
import "../styles/componentsStyles/StudentsList.css"

export default function StudentsList() {

    interface modalEffectProps {
        modalState: string;
        setModalState: (p: string) => void;
    }

    const modalEffect = useContext(ModalStateContext) as modalEffectProps;

    const [ checkedButtons, setCheckedButtons ] = useState([] as any)

    ///Change sayHello name

    function handleCallButton (id: string, isBlocked: boolean) {
        var buttonNum = document.getElementById(id);

        if(buttonNum?.className === "buttonUncheck" && isBlocked === false) {
            buttonNum?.classList.remove('buttonUncheck')
            buttonNum?.classList.add('buttonCheck')
            setCheckedButtons([...checkedButtons,id]);
            rollCallVerificantion();
            return;
        } if(isBlocked === true) {
            setCheckedButtons([...checkedButtons,id]);
            buttonClassRemover(id, true);
            rollCallVerificantion();
            return;
        }

        var oldButtons = checkedButtons;
        var newButtons = oldButtons.filter(x => x != id);
        setCheckedButtons(newButtons);    
        buttonClassRemover(id, false)
    }

    function handleNotPresentButton(id: string) {
        handleCallButton(id, true)
    }

    function buttonClassRemover(id:string, isBlocked: boolean) {
        var buttonNum = document.getElementById(id);

        if(isBlocked === true) {
            buttonNum?.classList.remove('buttonUnCheck')
            buttonNum?.classList.add('buttonBlock')
            return;
        }
            buttonNum?.classList.remove('buttonBlock')
            buttonNum?.classList.remove('buttonCheck')
            buttonNum?.classList.add('buttonUncheck')
    }

    function rollCallVerificantion() {
      if (checkedButtons.length === 7) {
        modalEffect.setModalState("modalCompleteOn");
        resetButtons();
      }
    }

    function resetButtons() {
        setCheckedButtons([]);

        for(var i = 1; i < 9; i++) {
            let buttonNum = String(i) as any;
            buttonClassRemover(buttonNum, false);
        }
    }

    return (
        <div className="studentsList">
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("1") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("1", false)} id="1"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("2") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("2", false)} id="2"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("3") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("3", false)} id="3"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("4") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("4", false)} id="4"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("5") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("5", false)} id="5"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("6") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("6", false)} id="6"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("7") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("7", false)} id="7"
                >
                    X
                </button>
            </div>
            <div className="student">
                <button 
                id="buttonImg" 
                onClick={ () => handleNotPresentButton("8") }
                >
                </button>
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button 
                className="buttonUncheck" 
                onClick={() => handleCallButton("8", false)} id="8"
                >
                    X
                </button>
            </div>

        </div>

    );
}

