import { useState } from "react";
import "../styles/componentsStyles/StudentsList.css"

export default function StudentsList() {

    const [buttonClass, setButtonClass] = useState("buttonUncheck");

    function handleCheckButton() {
        if(buttonClass === "buttonUncheck") {
           return setButtonClass("buttonCheck");
        }
        setButtonClass("buttonUnCheck");
    }

    return (
        <div className="studentsList">
            <div className="student">
                <img src="https://www.construtoracesconetto.com.br/wp-content/uploads/2020/03/blank-profile-picture-973460_640.png" alt="profileImg"/>
                <p>Name</p>
                <button className={buttonClass} onClick={handleCheckButton}>X</button>
            </div>

        </div>

    );
}
